mapper.select:
SELECT COUNT(MIDX)
FROM MANAGER_EMAIL A LEFT JOIN AFPS_MEMBER B ON A.TIDX = B.TIDX
WHERE A.DELYN = 'N' AND A.TIDX = #{userIdx}

SELECT * FROM (SELECT ROW_NUMBER() OVER (PARTITION BY A.DELYN ORDER BY MIDX DESC)NUM,
A.*,B.T_NAME,B.T_DEPARTMENT,B.T_POSITION FROM MANAGER_EMAIL A LEFT JOIN AFPS_MEMBER B ON A.TIDX = B.TIDX WHERE 1=1
AND A.TIDX = #{userIdx}
ORDER BY MIDX DESC)
WHERE DELYN = 'N' AND NUM BETWEEN #{rowStart} and #{rowEnd}

SELECT COUNT(BIDX)
FROM BOARD
WHERE BIDX > 0 AND DELYN= 'N' AND TIDX = #{userIdx}

SELECT COUNT(EIDX)
FROM ANNUAL_LEAVE
WHERE EIDX > 0 AND TIDX = #{userIdx}

select SIDX, S_TYPE, S_TITLE, S_STARTDATE, S_ENDDATE, S_CONTENT, DELYN, TIDX, T_NAME, T_ID
from (
SELECT A.*, B.T_NAME, T_ID, ROW_NUMBER() OVER(ORDER BY SIDX DESC) AS RNUM
FROM SCHEDULE_MANAGEMENT A LEFT JOIN AFPS_MEMBER B ON A.TIDX = B.TIDX WHERE A.TIDX = #{userIdx} 
AND A.DELYN = 'N') WHERE DELYN ='N' AND RNUM BETWEEN #{rowStart} AND #{rowEnd}

mapper.update:
UPDATE APPROVAL_LINE
SET TEAMLEADER = #{teamleader},
DEPARTMENTHEAD = #{departmenthead},
SECTIONHEAD = #{sectionhead}
WHERE EIDX = #{eidx}

UPDATE ANNUAL_LEAVE
SET A_TYPE = #{a_type},
A_STARTDATE = #{a_startdate},
A_ENDDATE = #{a_enddate},
A_USEDDAYS = #{a_useddays}
WHERE EIDX = #{eidx}
